sysauto_script.script:
var rightNow = new GlideDateTime();
var twentyForHoursFromNow = new GlideDateTime();
twentyForHoursFromNow.addDaysLocalTime(1);
gs.info(twentyForHoursFromNow.getDisplayValue());

var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
overdueNITasks.addQuery('due_date', '>=', rightNow);
overdueNITasks.addQuery('due_date', '<', twentyForHoursFromNow);
overdueNITasks.addQuery('state', '!=', 3);

overdueNITasks.query();

while(overdueNITasks.next()) {
	gs.info('Due soon NeedIt Task record = ' + overdueNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTask',overdueNITasks, overdueNITasks.number, gs.getUserName());
}
